using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Threading;
using System.Windows.Forms;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using nUpdate.Updating;

namespace nUpdate.UserInterfaceTest
{
    public class Settings
    {
        public string Uri;
        public string RsaKey;
        public string Language;
        public string Version;
        //[JsonProperty("Conditions")]
        public Dictionary<string, string> Conditions { get; set; }
    }

    public partial class MainDialog : Form
    {
        private readonly UpdaterUI _updaterUI;


        public MainDialog()
        {
            InitializeComponent();

            Settings settings = null;
            UpdateManager manager = null;
            using (manager)
            {
                // read json setting
                FileInfo settingsFile = new FileInfo(Environment.CurrentDirectory + @"\UserInterfaceTestSettings.json");
                if (settingsFile.Exists)
                {
                    settings = JsonConvert.DeserializeObject<Settings>(File.ReadAllText(settingsFile.FullName));

                    if (settings != null)
                    {
                        manager = new UpdateManager(
                            new Uri(settings.Uri),
                            settings.RsaKey,
                            new CultureInfo(settings.Language),
                            new UpdateVersion(settings.Version));
                        if (settings.Conditions.Any())
                        {
                            foreach (KeyValuePair<string, string> pair in settings.Conditions)
                            {
                                manager.Conditions = new List<KeyValuePair<string, string>> { pair };
                            }
                        }
                    }
                }

                if(settings == null)
                {
                    manager = new UpdateManager(
                        new Uri("http://localhost/nupdate/updates.json"),
                        "<RSAKeyValue><Modulus></Modulus><Exponent>AQAB</Exponent></RSAKeyValue>",
                        new CultureInfo("en"));
                }

                //UpdateManager 
                manager.RunInstallerAsAdmin = false;
                _updaterUI = new UpdaterUI(manager, SynchronizationContext.Current);
            }
        }

        private void updateButton_Click(object sender, EventArgs e)
        {
            _updaterUI.ShowUserInterface();
        }

        private void hiddenSearchCheckBox_CheckedChanged(object sender, EventArgs e)
        {
            _updaterUI.UseHiddenSearch = true;
        }
    }
}